package com.training.algo;

import java.util.HashMap;
import java.util.Map;

public class LongestPalindromeInString {

    private Map<String, String> cache = new HashMap<>();

    public String betterLongestPalindrome(String s) {
        if (s == null || s.length() < 1) return "";
        int start = 0, end = 0;
        for (int i = 0; i < s.length(); i++) {
            int len1 = expandAroundCenter(s, i, i);
            int len2 = expandAroundCenter(s, i, i + 1);
            int len = Math.max(len1, len2);
            if (len > end - start) {
                start = i - (len - 1) / 2;
                end = i + len / 2;
            }
        }
        return s.substring(start, end + 1);
    }

    private int expandAroundCenter(String s, int left, int right) {
        int L = left, R = right;
        while (L >= 0 && R < s.length() && s.charAt(L) == s.charAt(R)) {
            L--;
            R++;
        }
        return R - L - 1;
    }





    public String longestPalindrome(String s) {
        if (s.isEmpty()) {
            return s;
        }

        if (cache.containsKey(s)) {
            return cache.get(s);
        }

        String result = isPalindrome(s) ? s : "";
        for (int i=0; i < s.length(); i++) {
            for (int j=i; j < s.length(); j++) {
                String piece = i == j ? Character.toString(s.charAt(j)) : s.substring(i, j);
                if (isPalindrome(piece)) {
                    String rest = longestPalindrome(s.substring(j + 1));
                    result = maxByLength(result, maxByLength(piece, rest));
                }
            }
        }

        cache.put(s, result);
        return result;
    }







    private String maxByLength(String s1, String s2) {
        return s1.length() > s2.length() ? s1 : s2;
    }

    private boolean isPalindrome(String s) {
        for (int i=0; i < s.length()/2; i++) {
            if (s.charAt(i) != s.charAt(s.length()-i-1)) {
                return false;
            }
        }

        return true;
    }

    public static void main(String[] args) {
        LongestPalindromeInString longestPalindromeInString = new LongestPalindromeInString();
        System.out.println(longestPalindromeInString.longestPalindrome(""));
        System.out.println(longestPalindromeInString.betterLongestPalindrome(""));
    }
}
